const { execSync } = require('child_process');

const source = $input.first().json.source;
const cmd = `sshpass -p '${source.password}' ssh -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null ${source.user}@${source.host} "curl -fsSL https://raw.githubusercontent.com/SPeeDoA1/ServerPhoenix/main/ServerPhoenix/scanner.sh | bash -s /tmp/server-inventory.json"`;

try {
  const result = execSync(cmd, { encoding: 'utf-8', timeout: 120000 });
  return [{ json: { success: true, output: result, ...source } }];
} catch (e) {
  return [{ json: { success: false, error: e.message, ...source } }];
}

const { execSync } = require('child_process');

const source = $('Webhook').first().json.source;
const cmd = `sshpass -p '${source.password}' ssh -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null ${source.user}@${source.host} "curl -fsSL https://raw.githubusercontent.com/SPeeDoA1/ServerPhoenix/main/ServerPhoenix/backup.sh | bash -s /tmp/server-inventory.json ${source.user}"`;

try {
  const result = execSync(cmd, { encoding: 'utf-8', timeout: 600000 });
  return [{ json: { success: true, output: result } }];
} catch (e) {
  return [{ json: { success: false, error: e.message } }];
}

const { execSync } = require('child_process');

const source = $('Webhook').first().json.source;
const dest = $('Webhook').first().json.destination;

// Download from source to n8n server, then upload to destination
const cmd1 = `sshpass -p '${source.password}' scp -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null ${source.user}@${source.host}:/tmp/full-server-backup.tar.gz /tmp/phoenix-backup-$$.tar.gz`;
const cmd2 = `sshpass -p '${dest.password}' scp -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null /tmp/phoenix-backup-$$.tar.gz ${dest.user}@${dest.host}:/tmp/full-server-backup.tar.gz`;
const cmd3 = `rm -f /tmp/phoenix-backup-$$.tar.gz`;

try {
  execSync(cmd1, { encoding: 'utf-8', timeout: 600000 });
  execSync(cmd2, { encoding: 'utf-8', timeout: 600000 });
  execSync(cmd3, { encoding: 'utf-8' });
  return [{ json: { success: true, message: 'Transfer complete' } }];
} catch (e) {
  return [{ json: { success: false, error: e.message } }];
}

const { execSync } = require('child_process');

const dest = $('Webhook').first().json.destination;
const cmd = `sshpass -p '${dest.password}' ssh -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null ${dest.user}@${dest.host} "curl -fsSL https://raw.githubusercontent.com/SPeeDoA1/ServerPhoenix/main/ServerPhoenix/restore.sh | sudo bash -s /tmp/full-server-backup.tar.gz ${dest.user}"`;

try {
  const result = execSync(cmd, { encoding: 'utf-8', timeout: 900000 });
  return [{ json: { success: true, output: result } }];
} catch (e) {
  return [{ json: { success: false, error: e.message } }];
}

const { execSync } = require('child_process');

const dest = $('Webhook').first().json.destination;
const cmd = `sshpass -p '${dest.password}' ssh -o StrictHostKeyChecking=no -o UserKnownHostsFile=/dev/null ${dest.user}@${dest.host} "systemctl list-units --type=service --state=running --no-pager | head -20 && echo '' && ss -tlnp | head -15"`;

try {
  const result = execSync(cmd, { encoding: 'utf-8', timeout: 30000 });
  return [{ json: { success: true, services: result } }];
} catch (e) {
  return [{ json: { success: false, error: e.message } }];
}
